  }

  /* Better scrolling for Android */
  .android-scroll {
    -webkit-overflow-scrolling: touch;
    overscroll-behavior: contain;
  }

  /* Full screen support */
  @media (display-mode: standalone) {
    body {
      user-select: none;
      -webkit-user-select: none;
    }
  }

  /* Hide scrollbars in standalone mode */
  @media (display-mode: standalone) {
    ::-webkit-scrollbar {
      display: none;
    }
