                      <p className="text-xs text-blue-600 mt-2">1 day ago</p>
                    </div>
                  </div>
                </DialogContent>
              </Dialog>
            )}

            {/* Authentication Options */}
            {!isMobile && (
              <div className="flex items-center space-x-2">
                <Link href="/login">
                  <Button
                    variant="ghost"
                    size="sm"
                    className="min-w-[80px] min-h-[40px] px-3" 
                    data-testid="login-button"
