import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Button } from "@/components/ui/button";
import { TrendingUp, TrendingDown, Minus, Search, IndianRupee, AlertTriangle, RefreshCw } from "lucide-react";
import { getMarketPrices } from "@/lib/api";
import { useLanguage } from "@/components/language-provider";
import { ServiceConfigManager } from "@/lib/service-config";

export default function PriceTracker() {
  const [searchTerm, setSearchTerm] = useState("");
  const { t, language } = useLanguage();

  const { data: marketPrices, isLoading, error, refetch } = useQuery({
    queryKey: ["/api/market-prices", "realtime", language],
    queryFn: () => {
      const serviceConfig = ServiceConfigManager.getCurrentConfig();
      return getMarketPrices(undefined, true, serviceConfig);
    },
    refetchInterval: 2 * 60 * 1000, // Refetch every 2 minutes for real-time market data
    staleTime: 1 * 60 * 1000, // Consider data stale after 1 minute
  });

  // Listen for language changes and refresh data
  useEffect(() => {
    const handleLanguageChange = () => {
      refetch();
    };

    window.addEventListener('languageChanged', handleLanguageChange);
    return () => {
      window.removeEventListener('languageChanged', handleLanguageChange);
    };
  }, [refetch]);

  const handleRefreshPrices = () => {
    refetch();
  };

  const filteredPrices = marketPrices?.filter((price: any) =>
    price.cropName.toLowerCase().includes(searchTerm.toLowerCase())
  ) || [];

  const getTrendIcon = (trend: string) => {
    switch (trend) {
      case "up":
        return <TrendingUp className="w-4 h-4 text-primary" />;
      case "down":
        return <TrendingDown className="w-4 h-4 text-destructive" />;
      default:
        return <Minus className="w-4 h-4 text-muted-foreground" />;
    }
  };

  const getTrendColor = (trend: string) => {
    switch (trend) {
      case "up":
        return "text-primary";
      case "down":
        return "text-destructive";
      default:
        return "text-muted-foreground";
    }
  };

  return (
    <div className="min-h-screen bg-background py-8">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-foreground mb-4">{t("tools.priceTracker")}</h1>
          <p className="text-xl text-muted-foreground">
            {t("priceTracker.subtitle")}
          </p>
        </div>

        <div className="space-y-6">
          {/* Search and Filters */}
          <Card>
            <CardContent className="p-6">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
                <Input
                  placeholder={t("priceTracker.searchPlaceholder")}
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                  data-testid="input-search-crops"
                />
                <div className="mt-3 flex justify-end">
                  <Button 
                    onClick={handleRefreshPrices}
                    disabled={isLoading}
                    variant="outline"
                    size="sm"
                    className="gap-2"
                    data-testid="button-refresh-prices"
                  >
                    <RefreshCw className={`w-4 h-4 ${isLoading ? 'animate-spin' : ''}`} />
                    {t("common.refresh")}
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Market Overview Cards */}
          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-4">
            <Card>
              <CardContent className="p-6 text-center">
                <div className="text-2xl font-bold text-primary mb-2">â‚¹2.1L</div>
                <div className="text-sm text-muted-foreground">Avg Daily Trading</div>
                <div className="text-xs text-primary mt-1">â†— 5.2%</div>
              </CardContent>
            </Card>
            <Card>
              <CardContent className="p-6 text-center">
                <div className="text-2xl font-bold text-secondary mb-2">24</div>
                <div className="text-sm text-muted-foreground">Active Markets</div>
                <div className="text-xs text-muted-foreground mt-1">â†’ 0%</div>
              </CardContent>
            </Card>
            <Card>
              <CardContent className="p-6 text-center">
                <div className="text-2xl font-bold text-accent mb-2">89%</div>
                <div className="text-sm text-muted-foreground">Prediction Accuracy</div>
                <div className="text-xs text-accent mt-1">â†— 2.1%</div>
              </CardContent>
            </Card>
            <Card>
              <CardContent className="p-6 text-center">
                <div className="text-2xl font-bold text-primary mb-2">15+</div>
                <div className="text-sm text-muted-foreground">Supported Crops</div>
                <div className="text-xs text-primary mt-1">â†— 3 new</div>
              </CardContent>
            </Card>
          </div>

          {/* Price Table */}
          <Card>
            <CardHeader>
              <CardTitle>Current Market Prices</CardTitle>
            </CardHeader>
            <CardContent>
              {isLoading ? (
                <div className="space-y-4">
                  {Array.from({ length: 5 }).map((_, i) => (
                    <div key={i} className="flex items-center justify-between p-4 border border-border rounded-lg">
                      <div className="space-y-2">
                        <Skeleton className="h-4 w-24" />
                        <Skeleton className="h-3 w-16" />
                      </div>
                      <div className="text-right space-y-2">
                        <Skeleton className="h-4 w-20" />
                        <Skeleton className="h-3 w-12" />
                      </div>
                    </div>
                  ))}
                </div>
              ) : error ? (
                <div className="text-center py-8">
                  <AlertTriangle className="w-12 h-12 text-destructive mx-auto mb-4" />
                  <p className="text-muted-foreground">Failed to load market prices</p>
                  <p className="text-xs text-muted-foreground mt-1">Please try again later</p>
                </div>
              ) : (
                <div className="space-y-2">
                  {filteredPrices.map((price: any, index: number) => (
                    <div 
                      key={price.id || index} 
                      className="flex items-center justify-between p-4 border border-border rounded-lg hover:bg-muted/50 transition-colors"
                    >
                      <div className="flex items-center space-x-4">
                        <div className="w-10 h-10 bg-primary/10 rounded-lg flex items-center justify-center">
                          <span className="text-primary font-semibold text-sm">
                            {price.cropName.charAt(0)}
                          </span>
                        </div>
                        <div>
                          <h3 className="font-medium text-foreground" data-testid={`crop-name-${index}`}>
                            {price.cropName}
                          </h3>
                          <p className="text-sm text-muted-foreground">
                            {price.market} â€¢ {price.location}
                          </p>
                        </div>
                      </div>

                      <div className="text-right">
                        <div className="flex items-center space-x-2">
                          <IndianRupee className="w-4 h-4 text-muted-foreground" />
                          <span className="text-lg font-semibold text-foreground" data-testid={`price-value-${index}`}>
                            {price.price.toLocaleString()}
                          </span>
                          <span className="text-sm text-muted-foreground">/{price.unit}</span>
                        </div>
                        <div className={`flex items-center justify-end space-x-1 text-sm ${getTrendColor(price.trend)}`}>
                          {getTrendIcon(price.trend)}
                          <span data-testid={`price-trend-${index}`}>
                            {Math.abs(price.trendPercentage || 0).toFixed(1)}%
                          </span>
                        </div>
                      </div>
                    </div>
                  ))}

                  {filteredPrices.length === 0 && (
                    <div className="text-center py-8">
                      <Search className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                      <p className="text-muted-foreground">No crops found matching your search</p>
                    </div>
                  )}
                </div>
              )}
            </CardContent>
          </Card>

          {/* Market Insights */}
          <Card>
            <CardHeader>
              <CardTitle>AI Market Insights</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid md:grid-cols-3 gap-4">
                <div className="p-4 bg-primary/10 rounded-lg border border-primary/20">
                  <h4 className="font-medium text-foreground mb-2">Best Time to Sell</h4>
                  <p className="text-sm text-muted-foreground">
                    Wheat prices expected to rise 8% in next 2 weeks due to export demand
                  </p>
                </div>
                <div className="p-4 bg-secondary/10 rounded-lg border border-secondary/20">
                  <h4 className="font-medium text-foreground mb-2">Market Opportunity</h4>
                  <p className="text-sm text-muted-foreground">
                    Organic vegetables showing 15% premium in metro markets
                  </p>
                </div>
                <div className="p-4 bg-accent/10 rounded-lg border border-accent/20">
                  <h4 className="font-medium text-foreground mb-2">Risk Alert</h4>
                  <p className="text-sm text-muted-foreground">
                    Monsoon delays may affect rice prices in coming month
                  </p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/pages/weather-shield.tsx.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { 
  CloudRain, 
  Sun, 
  Wind, 
  Thermometer, 
  Droplets, 
  Eye,
  AlertTriangle,
  Info,
  MapPin
} from "lucide-react";
import { getWeatherData } from "@/lib/api";
import { useGeolocation } from "@/hooks/use-geolocation";
import { useLanguage } from "@/components/language-provider";
import { ServiceConfigManager } from "@/lib/service-config";

export default function WeatherShield() {
  const [location, setLocation] = useState("Mumbai");
  const [customLocation, setCustomLocation] = useState("");
  const { t, language } = useLanguage();
  const { latitude, longitude, error: locationError, getCurrentPosition } = useGeolocation();

  const { data: weatherData, isLoading, error, refetch } = useQuery({
    queryKey: ["/api/weather", location, "realtime", language],
    queryFn: () => {
      const serviceConfig = ServiceConfigManager.getCurrentConfig();
      return getWeatherData(location, true, serviceConfig);
    },
    refetchInterval: 5 * 60 * 1000, // Refetch every 5 minutes for real-time updates
    staleTime: 2 * 60 * 1000, // Consider data stale after 2 minutes
  });

  // Listen for language changes and refresh data
  useEffect(() => {
    const handleLanguageChange = () => {
      refetch();
    };

    window.addEventListener('languageChanged', handleLanguageChange);
    return () => {
      window.removeEventListener('languageChanged', handleLanguageChange);
    };
  }, [refetch]);

  const handleLocationSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (customLocation.trim()) {
      setLocation(customLocation.trim());
      // Force refetch for new location
      setTimeout(() => refetch(), 100);
    }
  };

  const handleRefreshWeather = () => {
    refetch();
  };

  const handleUseCurrentLocation = async () => {
    if (latitude && longitude) {
      // Convert coordinates to location name using reverse geocoding
      try {
        const response = await fetch(`https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${latitude}&longitude=${longitude}&localityLanguage=en`);
        const data = await response.json();
        const city = data.city || data.locality || data.principalSubdivision || "Current Location";
        setLocation(city);
      } catch (error) {
        setLocation(`${latitude.toFixed(2)},${longitude.toFixed(2)}`);
      }
    } else {
      getCurrentPosition();
    }
  };

  const getAlertVariant = (severity: string) => {
    switch (severity.toLowerCase()) {
      case "high":
      case "severe":
        return "border-destructive/20 bg-destructive/10";
      case "medium":
      case "moderate":
        return "border-secondary/20 bg-secondary/10";
      default:
        return "border-accent/20 bg-accent/10";
    }
  };

  const mockAlerts = [
    {
      type: "severe",
      title: "Heavy Rainfall Warning",
      description: "Intense rainfall expected in next 6 hours. Secure crops and equipment.",
      validUntil: "2024-12-02T18:00:00Z",
      icon: CloudRain
    },
    {
      type: "moderate", 
      title: "High Wind Advisory",
      description: "Wind speeds up to 45 km/h. Monitor tall crops for damage.",
      validUntil: "2024-12-02T22:00:00Z", 
      icon: Wind
    },
    {
      type: "info",
      title: "Optimal Harvesting Conditions",
      description: "Clear skies and low humidity perfect for harvesting operations.",
      validUntil: "2024-12-03T12:00:00Z",
      icon: Sun
    }
  ];

  const forecast = [
    { day: "Today", high: 32, low: 24, condition: "Sunny", icon: Sun, rain: 0 },
    { day: "Tomorrow", high: 29, low: 22, condition: "Cloudy", icon: CloudRain, rain: 60 },
    { day: "Wednesday", high: 27, low: 20, condition: "Rainy", icon: CloudRain, rain: 85 },
    { day: "Thursday", high: 30, low: 23, condition: "Partly Cloudy", icon: Sun, rain: 20 },
    { day: "Friday", high: 33, low: 25, condition: "Sunny", icon: Sun, rain: 5 },
  ];

  return (
    <div className="min-h-screen bg-background py-8">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-foreground mb-4">Weather Shield</h1>
          <p className="text-xl text-muted-foreground">
            Advanced weather monitoring and crop protection alerts
          </p>
        </div>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* Main Weather Section */}
          <div className="lg:col-span-2 space-y-6">
            {/* Location Input */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <MapPin className="w-5 h-5" />
                  <span>Location</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                <form onSubmit={handleLocationSubmit} className="flex space-x-2">
                  <Input
                    placeholder="Enter city name..."
                    value={customLocation}
                    onChange={(e) => setCustomLocation(e.target.value)}
                    data-testid="input-location"
                  />
                  <Button type="submit" data-testid="button-search-location">
                    Search
                  </Button>
                  <Button 
                    type="button" 
                    variant="outline"
                    onClick={handleUseCurrentLocation}
                    data-testid="button-use-current-location"
                  >
                    Use GPS
                  </Button>
                  <Button 
                    type="button" 
                    variant="outline"
                    onClick={handleRefreshWeather}
                    disabled={isLoading}
                    data-testid="button-refresh-weather"
                  >
                    ðŸ”„ Refresh
                  </Button>
                </form>
                {locationError && (
                  <p className="text-sm text-destructive mt-2">{locationError}</p>
                )}
              </CardContent>
            </Card>

            {/* Current Weather */}
            <Card>
              <CardHeader>
                <CardTitle>Current Weather - {location}</CardTitle>
              </CardHeader>
              <CardContent>
