        cotton: { base: 5800, markets: ['Gujarat', 'Maharashtra', 'AP'], seasonal: 0.8 },
        soybean: { base: 4300, markets: ['MP', 'Maharashtra', 'Rajasthan'], seasonal: 1.1 },
        potato: { base: 1400, markets: ['UP', 'Bihar', 'West Bengal'], seasonal: 1.3 },
